import React, { useState, useEffect } from "react";
import {
    StyleSheet,
    Text,
    View,
    ScrollView,
    Image,
    TouchableHighlight,
    TouchableOpacity,
} from "react-native";
import EmployerBottomNavigator from './EmployerBottomNavigator';
import UserContactOverlay from './UserContactOverlay';
import EmployerSettingsOverlay from './EmployerSettingsOverlay';
import AsyncStorage from "@react-native-community/async-storage";
import { baseUrl } from "../../utils/constants";
import { AntDesign } from "@expo/vector-icons";

const EmployerProfile1 = (props) => {
    const [modalVisible, setModalVisible] = useState(false);
    const [settingModalVisible, setSettingModalVisible] = useState(false);
    const [employerLoginData, setEmployerLoginData] = useState({});

    function switchToScreen(screen, params) {
        props.navigation.navigate(screen, params);
    }

    useEffect(() => {
        try {
            AsyncStorage.getItem("employerData").then((employer) => {
                setEmployerLoginData(JSON.parse(employer));
            });
        } catch (error) {
            // Error retrieving data
            console.log(error.message);
        }
    }, [])

    console.log(employerLoginData)

    const {
        image,
        company_name,
        first_name,
        last_name,
        phone_number,
        email,
    } = employerLoginData;

    return (
        <View style={styles.container}>
            <View style={styles.innerContainer}>
                <ScrollView>
                    <View style={{ flexDirection: "column" }}>
                        <View style={styles.backDrop}></View>
                        <View style={styles.imageContainer}>
                            {image ? (
                                <Image
                                    style={styles.profileImage}
                                    source={{ uri: image }}
                                />
                            ) : (
                                    <Image
                                        source={{
                                            uri:
                                                "https://i.ya-webdesign.com/images/blank-profile-picture-png-8.png",
                                        }}
                                        style={styles.profileImage}
                                    />
                                )}
                        </View>
                        <View style={styles.metaContainer}>
                            {employerLoginData !== null ? (
                                <View style={styles.subMetaDataContainer}>
                                    <View>
                                        <Text style={[styles.companyName]}>
                                            {company_name}
                                        </Text>
                                    </View>
                                    <View style={styles.nameContainer}>
                                        <Text style={[styles.boldText]}>
                                            {first_name}
                                        </Text>
                                        <Text style={[styles.boldText]}>
                                            {last_name}
                                        </Text>
                                    </View>
                                    <View>
                                        <Text style={[styles.boldText]}>
                                            {phone_number}
                                        </Text>
                                    </View>
                                </View>
                            ) : null}
                            <View style={styles.actionContainer}>
                                <TouchableHighlight
                                    style={styles.starBtn}
                                    onPress={() =>
                                        alert(
                                            "Contact overlay should be displayed."
                                        )
                                    }
                                >
                                    <AntDesign
                                        name="staro"
                                        style={styles.starBtnIcon}
                                    />
                                </TouchableHighlight>
                                <TouchableOpacity
                                    style={styles.contactBtn}
                                    onPress={() => {
                                        setModalVisible(true);
                                    }}
                                >
                                    <Text style={styles.contactBtnText}>
                                        Contact
                                    </Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </View>
                </ScrollView>
            </View>
            <View style={styles.bottomNavigation}>
                <EmployerBottomNavigator
                    employerLoginData={employerLoginData}
                    setSettingModalVisible={setSettingModalVisible}
                    switchToScreen={switchToScreen}
                />
            </View>
            <UserContactOverlay
                mobile={phone_number}
                email={email}
                modalVisible={modalVisible}
                setModalVisible={setModalVisible}
            />
            <EmployerSettingsOverlay
                settingModalVisible={settingModalVisible}
                setSettingModalVisible={setSettingModalVisible}
            />
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flexDirection: "column",
        flex: 1,
        width: null,
        height: null,
    },
    innerContainer: {
        flex: 9.2,
    },
    bottomNavigation: {
        flex: 0.8,
        backgroundColor: "#dddddd",
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 8,
    },
    loginText: {
        color: "black",
        fontSize: 30,
        marginTop: 20,
        marginLeft: 100,
    },
    nextBtnText: {
        color: "black",
        fontSize: 20,
    },
    loginBtn: {
        width: "20%",
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 550,
        marginBottom: 100,
        marginLeft: 300,
    },
    backDrop: {
        height: 170,
        backgroundColor: "#f8bbd0",
    },
    imageContainer: {
        height: 120,
        width: 120,
        borderColor: "#dddddd",
        backgroundColor: "#ffffff",
        borderRadius: 100,
        marginLeft: 15,
        marginTop: 15,
        position: "absolute",
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 10,
    },
    metaContainer: {
        backgroundColor: "#f8bbd0",
        position: "absolute",
        marginTop: 30,
        width: "100%",
        paddingBottom: 10,
    },
    subMetaDataContainer: {
        marginLeft: 150,
        width: "100%",
    },
    nameContainer: {
        marginTop: 5,
        flexDirection: "row",
    },
    companyName: {
        marginRight: 5,
        fontWeight: "700",
        fontSize: 18,
    },
    boldText: {
        fontWeight: "700",
    },
    profileImage: {
        height: 120,
        width: 120,
    },
    actionContainer: {
        height: 60,
        width: 200,
        flexDirection: "row",
        justifyContent: "flex-end",
        width: "100%",
    },
    contactBtn: {
        width: "40%",
        height: 40,
        backgroundColor: "#8fbc8f",
        borderRadius: 25,
        justifyContent: "center",
        alignItems: "center",
        alignSelf: "flex-end",
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 10,
        marginHorizontal: 15,
    },
    contactBtnText: {
        color: "white",
        fontSize: 16,
    },
    starBtn: {
        width: 40,
        backgroundColor: "#ffee58",
        borderRadius: 25,
        alignSelf: "flex-end",
        marginHorizontal: 5,
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 10,
    },
    starBtnIcon: {
        fontSize: 30,
        padding: 5,
        color: "#9E9E9E",
    },
})

export default EmployerProfile1;
