import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { FontAwesome5 } from "@expo/vector-icons";
import { LinearGradient } from "expo-linear-gradient";

const SuccessScreen = (props) => {
    const [screenToNavigate, setScreenToNavigate] = useState(null);
    useEffect(() => {
        setScreenToNavigate(props.navigation.state.params);
        // props.navigation.navigate("EmployerScreen")
    }, [])

    useEffect(() => {
        if (screenToNavigate) {
            setTimeout(() => {
                props.navigation.navigate(screenToNavigate.screen);
            }, 3000)
        } else {
            setTimeout(() => {
                props.navigation.navigate("EmployerScreen");
            }, 3000)
        }
    }, [screenToNavigate])

    return (
        <LinearGradient
            colors={["#BA9ED1", "#765A8D"]}
            style={styles.container}
            //  Linear Gradient
            start={{ x: 0, y: 0 }}
            end={{ x: 0, y: 1 }}
        >
            <FontAwesome5 name="check-circle" style={styles.checkIcon} />
        </LinearGradient>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
    },
    checkIcon: {
        fontSize: 200,
        color: "#C7E8AC",
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 8,
    }
})

export default SuccessScreen;
