import React from "react";
import {
    StyleSheet,
    Text,
    View,
    TextInput,
    TouchableOpacity,
    ScrollView,
} from "react-native";
// import DatePicker from 'react-native-datepicker'

import { baseUrl } from "../utils/constants";
export default class JobPost extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            job_title: "",
            job_description: "",
            start_date: "",
            end_date: "",
            wager_offered: "",
            job_service_id: "",
            success: false,
            err: "",
            user_id: 0,
        };
    }
    componentDidMount() {
        this.setState({
            user_id: this.props.navigation.getParam("user_id"),
        });
    }
    handleSubmit = () => {
        this.setState({
            job_service_id: parseInt(this.state.job_service_id),
        });

        fetch(baseUrl + "api/employer/post-job", {
            method: "POST",
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json",
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer",
            body: JSON.stringify(this.state),
        })
            .then((response) => response.json())
            .then((response) => {
                console.log(response);
                if (!response.success) throw new Error(response);
                else {
                    this.setState({
                        success: true,
                        message: "Job Post was successful",
                    });
                }
            })
            .catch((err) => {
                this.setState({
                    success: false,
                    err: "Registration Failed. Please try again later",
                });
            });
    };

    render() {
        return (
            <ScrollView>
                <View>
                    <Text style={styles.loginText}>Post a new Job</Text>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Job Title</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            onChangeText={(text) => {
                                this.setState({ job_title: text });
                            }}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Job Description</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            onChangeText={(text) => {
                                this.setState({ job_description: text });
                            }}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Start Date</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            onChangeText={(text) => {
                                this.setState({ start_date: text });
                            }}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>End Date</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            onChangeText={(text) => {
                                this.setState({ end_date: text });
                            }}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Wager Offered</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            onChangeText={(text) => {
                                this.setState({ wager_offered: text });
                            }}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Select Service</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            onChangeText={(text) => {
                                this.setState({ job_service_id: text });
                            }}
                        />
                    </View>
                </View>
                {this.state.success ? (
                    <View style={styles.showMessage}>
                        <Text>{this.state.message}</Text>
                    </View>
                ) : this.state.err ? (
                    <View style={styles.showMessage}>
                        <Text>{this.state.message}</Text>
                    </View>
                ) : null}
                <View style={styles.loginBtn1}>
                    <TouchableOpacity onPress={this.handleSubmit}>
                        <Text style={styles.nextBtnText}>Submit</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#ffffff",
        alignItems: "center",
        justifyContent: "center",
    },
    showMessage: {
        justifyContent: "center",
        marginTop: 30,
        marginLeft: 40,
    },
    error: {
        color: "red",
        fontSize: 12,
        marginTop: 20,
        marginLeft: 35,
        justifyContent: "center",
    },
    nextBtnText: {
        color: "black",
        fontSize: 20,
    },
    inputText: {
        height: 30,
        color: "black",
        marginTop: -17,
        marginLeft: -50,
    },
    inputText1: {
        height: 30,
        color: "black",
        marginTop: 50,
        marginLeft: 40,
    },
    loginText: {
        color: "black",
        fontSize: 30,
        marginTop: 20,
        //marginBottom: 100,
        marginLeft: 65,
    },
    inputView2: {
        marginBottom: -35,
    },
    firstnameText: {
        flexDirection: "row",
        width: "45%",
        backgroundColor: "#64358D",
        borderRadius: 25,
        height: 30,
        marginTop: -40,
        marginLeft: 160,
        marginBottom: 10,

        justifyContent: "center",
        padding: 20,
    },
    loginBtn: {
        width: "20%",
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: -50,
        //marginBottom: 100,
        marginLeft: 285,
    },
    loginBtn1: {
        width: "20%",
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 150,
        //marginBottom: 100,
        marginLeft: 85,
    },
});
