import React from 'react';
import _ from "lodash";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ScrollView
} from 'react-native';

import { baseUrl } from '../utils/constants';
const styles = StyleSheet.create({

})

// make the GET request to fetch data from the URL then using promise function to handle response.
class BasicInfo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      first_name: "",
      last_name: "",
      email: "",
      password: "",
      company_name: "",
      confirm_email: "",
      phone_number: "",
      title: "",
      success: true,
      // error:{
      first_name_error: null,
      last_name_error: null,
      campany_error: null,
      email_error: null,
      confirm_email_error: null,
      password_error: null,
      mobile_error: null,
      // }
    }
  }
  handleTitleChange = (event) => {

    this.setState({
      title: event
    })
  }
  handleFirstNameChange = (event) => {
    console.log("sedfhghfhjgjg",event)
    if (_.isEmpty(event)) {
      this.setState({ first_name_error: "name is required" })
    }
    this.setState({
      first_name: event
    })
  }
  handleLastNameChange = (event) => {

    this.setState({
      last_name: event
    })
  }
  handleEmailChange = (event) => {
    this.setState({
      email: event
    })
  }
  handlePhoneNumberChange = (event) => {

    this.setState({
      phone_number: event
    })
  }
  handleConfirmEmailChange = (event) => {

    this.setState({
      confirm_email: event
    })
  }
  handleCompanyChange = (event) => {

    this.setState({
      company_name: event
    })
  }

  handleSubmit = () => {
    if (_.isEmpty(this.state.first_name)) {
      console.log("dgffdhggfhfhfh")
      this.setState({ first_name_error: "name is required" },()=>{
        console.log("fsdfdgdgdg",this.state)
      })
    }
    // this.props.navigation.navigate('AddInfo')
    // const that = this
    // // that.props.navigation.navigate('AddInfo')
    // fetch(baseUrl + 'api/employer/registration', {
    //   method: 'POST',
    //   mode: 'cors', // no-cors, *cors, same-origin
    //   cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    //   credentials: 'same-origin', // include, *same-origin, omit
    //   headers: {
    //     'Content-Type': 'application/json'
    //     // 'Content-Type': 'application/x-www-form-urlencoded',
    //   },
    //   redirect: 'follow', // manual, *follow, error
    //   referrerPolicy: 'no-referrer',
    //   body: JSON.stringify(this.state)
    // })
    //   .then(response => response.json())
    //   .then((response) => {
    //     console.log("efrgfhgfghfhf", response)
    //     if (!response.success)
    //       throw new Error(response)
    //     else {
    //       that.props.navigation.navigate('AddInfo', { user_id: response.result.id })
    //     }
    //   })
    //   .catch((err) => {
    //     this.setState({ success: false, err: "Registration Failed. Please try again later" })
    //   })
  }

  render() {
    return (
      <ScrollView style={styles.rowCantainer}>
        <View  style={styles.labelHeading}>
          <Text style={styles.loginText}>Basic Information</Text>
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1} >Title/Prefix</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              placeholder="Mr/Mrs/Ms "
              placeholderTextColor="white"
              onChangeText={this.handleTitleChange}
            />
          </View>
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>First Name</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              placeholder="John"
              name="first_name"
              placeholderTextColor="white"
              onChangeText={this.handleFirstNameChange}
            />
          </View>
           {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>Last Name</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              placeholder="Doe"
              placeholderTextColor="white"
              onChangeText={this.handleLastNameChange}
            />
          </View>
          {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>Company Name</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              placeholder="Company Name Ltd"
              placeholderTextColor="white"
              onChangeText={this.handleCompanyChange}
            />
          </View>
          {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>Email</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              placeholder="john@example.com"
              placeholderTextColor="white"
              onChangeText={this.handleEmailChange}
            />
          </View>
          {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>Confirm Email</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              placeholder=" "
              placeholderTextColor="white"
              onChangeText={this.handleConfirmEmailChange}
            />
          </View>
          {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>Password</Text>
          <View style={styles.firstnameText}>
            <TextInput
              name="password"
              type="text"
              secureTextEntry
              style={styles.inputText}
              placeholder="***********"
              placeholderTextColor="white"
              onChangeText={(text) => { this.setState({ password: text }) }}
            />
          </View>
          {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
        </View>
        <View style={styles.inputView2}>
          <Text style={styles.inputText1}>Phone Number</Text>
          <View style={styles.firstnameText}>

            <TextInput
              style={styles.inputText}
              placeholder=" "
              placeholderTextColor="white"
              onChangeText={this.handlePhoneNumberChange}
            />
          </View>
          {this.state.first_name_error ? (
              <Text style={styles.errorText}>{this.state.first_name_error}</Text>
            ) : null}
          {
            !this.state.success && <View><Text style={styles.error}>{this.state.err}</Text></View>
          }
        </View>

        <View style={styles.loginBtn1}>
          <TouchableOpacity onPress={this.handleSubmit}>
            <Text style={styles.nextBtnText}>Submit</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    )
  }
}
export default BasicInfo;
