import React, { useState } from "react";
import { StyleSheet, Text, View, Image, TouchableOpacity } from "react-native";
import { AntDesign } from "@expo/vector-icons";

const UserCard = ({
    applicant,
    switchToScreen,
    setModalVisible,
    setUserRecord,
}) => {
    console.log("applicant list ===========> ", applicant);
    const [isDescriptionVisible, setDescriptionVisible] = useState(false);
    const {
        apply_jobs,
        city,
        company_name,
        created_at,
        first_name,
        id,
        image,
        last_name,
        phone_number,
        title,
        updated_at,
        user_id
    } = applicant;

    return (
        <View style={styles.container}>
            <View
                style={styles.jobContainer}
                onStartShouldSetResponder={(event) => {
                    setDescriptionVisible(!isDescriptionVisible);
                }}
            >
                <View style={styles.imageContainer}>
                    <Image
                        source={{
                            uri:
                                "https://i.ya-webdesign.com/images/blank-profile-picture-png-8.png",
                        }}
                        style={styles.profileImage}
                    />
                </View>
                <View style={styles.detailContainer}>
                    <View style={styles.nameContainer}>
                        <Text style={[styles.jobSeekerName]}>
                            {title}
                        </Text>
                        <Text style={[styles.jobSeekerName]}>
                            {first_name}
                        </Text>
                        <Text style={[styles.jobSeekerName]}>
                            {last_name}
                        </Text>
                    </View>
                    <Text style={styles.subTitleText}>
                        {company_name}
                    </Text>
                    <Text>
                        {city}
                    </Text>
                    <Text>
                        {phone_number}
                    </Text>
                </View>
            </View>
            {isDescriptionVisible ? (
                <View style={styles.toggleableView}>
                    <TouchableOpacity
                        style={styles.detailBtn}
                        onPress={() => {
                            try {
                                AsyncStorage.setItem("jobSeekersList", JSON.stringify(job_seekers))
                            } catch (error) {
                                // Error retrieving data
                                console.log(error.message);
                            }
                            switchToScreen("JobSeekerProfile1", applicant)
                        }}
                    >
                        <Text style={styles.detailBtnText}>View Profile</Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.contactBtn}
                        onPress={() => {
                            setUserRecord(applicant);
                            setModalVisible(true);
                        }}
                    >
                        <Text style={styles.contactBtnText}>Contact</Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.starBtn}
                        onPress={() => console.log("Clicked")}
                    >
                        <AntDesign name="staro" style={styles.starBtnIcon} />
                    </TouchableOpacity>
                </View>
            ) : null}
        </View>
    )
}

export default UserCard;

const styles = StyleSheet.create({
    container: {
        minHeight: 150,
        borderWidth: 1,
        borderColor: "#dddddd",
        backgroundColor: "#ffffff",
        borderRadius: 10,
        marginLeft: 70,
        margin: 15,
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 8,
        flexDirection: "column",
    },
    jobContainer: {
        flexDirection: "row",
    },
    imageContainer: {
        height: 120,
        width: 120,
        borderColor: "#dddddd",
        backgroundColor: "#ffffff",
        borderRadius: 100,
        marginLeft: -60,
        marginRight: 10,
        marginVertical: 15,
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 10,
    },
    detailContainer: {
        paddingVertical: 5,
        width: 0,
        flexGrow: 1,
        flex: 1,
    },
    toggleableView: {
        minHeight: 46,
        padding: 5,
        flexDirection: "row",
        justifyContent: "space-around",
    },
    profileImage: {
        height: 120,
        width: 120,
    },
    nameContainer: {
        marginTop: 5,
        flexDirection: "row",
    },
    jobSeekerName: {
        marginRight: 5,
        fontWeight: "700",
        fontSize: 18,
    },
    titleText: {
        fontSize: 15,
        fontWeight: "800",
        textDecorationLine: "underline",
    },
    subTitleText: {
        fontWeight: "600",
        fontSize: 13,
    },
    detailBtn: {
        width: "40%",
        height: 36,
        backgroundColor: "#8fbc8f",
        borderRadius: 25,
        justifyContent: "center",
        alignItems: "center",
        alignSelf: "flex-end",
    },
    detailBtnText: {
        color: "white",
    },
    contactBtn: {
        width: "40%",
        height: 36,
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        justifyContent: "center",
        alignItems: "center",
        alignSelf: "flex-end",
    },
    contactBtnText: {
        color: "white",
    },
    starBtn: {
        width: 36,
        backgroundColor: "#ffee58",
        borderRadius: 25,
        alignSelf: "flex-end",
    },
    starBtnIcon: {
        fontSize: 30,
        padding: 3,
        color: "#9E9E9E",
    },
})
