import React from 'react';
import _ from "lodash";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ScrollView
} from 'react-native';

import { baseUrl } from '../utils/constants';
const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
    flex: 1,
    width: null,
    height: null,
  },
  innerContainer: {
    flex: 9.2,
  },
  bottomNavigation: {
    flex: 0.8,
    backgroundColor: "#dddddd",
    shadowColor: "#dddddd",
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4.65,
    elevation: 8,
  },
  rowContainer: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 20,
    marginVertical: 10,
  },
  labelText: {
    flex: 1,
    fontWeight: "700",
    fontSize: 16,
  },
  inputContainer: {
    flex: 2,
  },
  pickerStyle: {
    flex: 2,
    height: 50,
    overflow: "hidden",
    borderRadius: 5,
    backgroundColor: "#ffffff",
    shadowColor: "#dddddd",
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4.65,
    elevation: 4,
  },
  inputStyle: {
    flex: 2,
    height: 50,
    overflow: "hidden",
    borderRadius: 5,
    backgroundColor: "#ffffff",
    paddingLeft: 10,
    shadowColor: "#dddddd",
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4.65,
    elevation: 4,
  },
  imageContainer: {
    flexDirection: "column",
    margin: 15,
    justifyContent: "center",
    alignItems: "center",
  },
  imagePlaceholder: {
    backgroundColor: "#ffffff",
    borderRadius: 100,
    shadowColor: "#dddddd",
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4.65,
    elevation: 8,
  },
  profileImage: {
    height: 120,
    width: 120,
    borderRadius: 100,
    alignSelf: "center",
  },
  errorText: {
    textTransform: "capitalize",
    color: "red",
    marginLeft: 5
  },
  updateBtn: {
    height: 50,
    width: 180,
    borderRadius: 25,
    backgroundColor: "#fb5b5a",
    shadowColor: "#dddddd",
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4.65,
    elevation: 8,
    alignItems: "center",
    justifyContent: "center",
  },
  loginText: {
    color: 'black',
    // fontSize: 30,
    // marginTop: 20,
    // marginLeft: 65,
  },
})

// make the GET request to fetch data from the URL then using promise function to handle response.
class BasicInfo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      first_name: "",
      last_name: "",
      email: "",
      password: "",
      company_name: "",
      confirm_email: "",
      phone_number: "",
      title: "",
      success: true,
      // error:{
      first_name_error: null,
      last_name_error: null,
      campany_error: null,
      email_error: null,
      confirm_email_error: null,
      password_error: null,
      mobile_error: null,
      // }
    }
  }
  handleTitleChange = (event) => {

    this.setState({
      title: event
    })
  }
  handleFirstNameChange = (event) => {
    console.log("sedfhghfhjgjg", event)
    if (_.isEmpty(event)) {
      this.setState({ first_name_error: "name is required" })
    }
    this.setState({
      first_name: event
    })
  }
  handleLastNameChange = (event) => {

    this.setState({
      last_name: event
    })
  }
  handleEmailChange = (event) => {
    this.setState({
      email: event
    })
  }
  handlePhoneNumberChange = (event) => {

    this.setState({
      phone_number: event
    })
  }
  handleConfirmEmailChange = (event) => {

    this.setState({
      confirm_email: event
    })
  }
  handleCompanyChange = (event) => {

    this.setState({
      company_name: event
    })
  }

  handleSubmit = () => {
    if (_.isEmpty(this.state.first_name)) {
      console.log("dgffdhggfhfhfh")
      this.setState({ first_name_error: "name is required" }, () => {
        console.log("fsdfdgdgdg", this.state)
      })
    }
    // this.props.navigation.navigate('AddInfo')
    // const that = this
    // // that.props.navigation.navigate('AddInfo')
    // fetch(baseUrl + 'api/employer/registration', {
    //   method: 'POST',
    //   mode: 'cors', // no-cors, *cors, same-origin
    //   cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    //   credentials: 'same-origin', // include, *same-origin, omit
    //   headers: {
    //     'Content-Type': 'application/json'
    //     // 'Content-Type': 'application/x-www-form-urlencoded',
    //   },
    //   redirect: 'follow', // manual, *follow, error
    //   referrerPolicy: 'no-referrer',
    //   body: JSON.stringify(this.state)
    // })
    //   .then(response => response.json())
    //   .then((response) => {
    //     console.log("efrgfhgfghfhf", response)
    //     if (!response.success)
    //       throw new Error(response)
    //     else {
    //       that.props.navigation.navigate('AddInfo', { user_id: response.result.id })
    //     }
    //   })
    //   .catch((err) => {
    //     this.setState({ success: false, err: "Registration Failed. Please try again later" })
    //   })
  }

  render() {
    return (
      <View style={styles.container}>
        <View style={styles.innerContainer}>
          <ScrollView>
            <View>
              <Text style={styles.loginText}>Basic Information</Text>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText} >Title/Prefix</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.inputStyle}
                  placeholder="Mr/Mrs/Ms "
                  placeholderTextColor="white"
                  onChangeText={this.handleTitleChange}
                />
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>First Name</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.inputStyle}
                  placeholder="John"
                  name="first_name"
                  placeholderTextColor="white"
                  onChangeText={this.handleFirstNameChange}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>Last Name</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.inputStyle}
                  placeholder="Doe"
                  placeholderTextColor="white"
                  onChangeText={this.handleLastNameChange}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>Company Name</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.inputStyle}
                  placeholder="Company Name Ltd"
                  placeholderTextColor="white"
                  onChangeText={this.handleCompanyChange}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>Email</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.inputStyle}
                  placeholder="john@example.com"
                  placeholderTextColor="white"
                  onChangeText={this.handleEmailChange}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>Confirm Email</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.inputStyle}
                  placeholder=" "
                  placeholderTextColor="white"
                  onChangeText={this.handleConfirmEmailChange}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>Password</Text>
              <View style={styles.inputContainer}>
                <TextInput
                  name="password"
                  type="text"
                  secureTextEntry
                  style={styles.inputStyle}
                  placeholder="***********"
                  placeholderTextColor="white"
                  onChangeText={(text) => { this.setState({ password: text }) }}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
            </View>
            <View style={styles.rowContainer}>
              <Text style={styles.labelText}>Phone Number</Text>
              <View style={styles.inputContainer}>

                <TextInput
                  style={styles.inputStyle}
                  placeholder=" "
                  placeholderTextColor="white"
                  onChangeText={this.handlePhoneNumberChange}
                />
                {this.state.first_name_error ? (
                  <Text style={styles.errorText}>{this.state.first_name_error}</Text>
                ) : null}
              </View>
              {
                !this.state.success && <View><Text style={styles.error}>{this.state.err}</Text></View>
              }
            </View>

            <View style={styles.rowContainer}>
              <TouchableOpacity  style={styles.updateBtn} onPress={this.handleSubmit}>
                <Text>Submit</Text>
              </TouchableOpacity>
            </View>
          </ScrollView>

        </View>
      </View>
    )
  }
}
export default BasicInfo;
