import React from "react";
import { StyleSheet, Text, View, TouchableOpacity } from "react-native";
import { LinearGradient } from "expo-linear-gradient";
import { FontAwesome } from "@expo/vector-icons";
import { baseUrl } from "../utils/constants";
import { PayPal } from 'rn-expo-paypal-integration';

export default function PaymentScreen(props) {
    function initiatePaypal() {
        const api_url = baseUrl + `api/paypal`;
        fetch(api_url, {
            method: "GET",
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json",
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer",
        })
            .then((response) => response.json())
            .then((response) => {
                if (response.success) {
                    props.navigation.navigate("Browser", {
                        url: response.url,
                    });
                } else {
                    throw new Error(response);
                }
            })
            .catch((err) => {
                console.log(err);
            });
    }

    return (
        <LinearGradient
            colors={["#BA9ED1", "#765A8D"]}
            style={styles.container}
            //  Linear Gradient
            start={{ x: 0, y: 0 }}
            end={{ x: 0, y: 1 }}
        >
            <Text>Payment Screen</Text>
            <TouchableOpacity
                style={styles.paymentBtn}
                onPress={initiatePaypal}
            >
                <FontAwesome name="paypal" color="#ffffff" />
                <Text style={{ color: "#ffffff", marginLeft: 10 }}>
                    Pay with PayPal
                </Text>
            </TouchableOpacity>
            <View>
                <PayPal
                    amount={20}//i.e $20 
                    orderID={'paypal-298'} //transactionID
                    ProductionClientID={'paypal-298'}
                    success={(a) => {
                        //callback after payment has been successfully compleated
                        console.log(a)
                    }}
                    failed={(a) => {
                        //callback if payment is failed
                    }}
                />
            </View>
        </LinearGradient>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
    },
    paymentBtn: {
        width: "70%",
        backgroundColor: "#4267b2",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 10,
        marginBottom: 10,
        flexDirection: "row",
    },
});
