import React, { useEffect } from "react";
import { View, Text, StyleSheet } from "react-native";
import { AntDesign, Ionicons } from "@expo/vector-icons";
import AsyncStorage from "@react-native-community/async-storage";
import _ from "lodash";

const EmployerBottomNavigator = ({
    employerLoginData,
    switchToScreen,
    setSettingModalVisible,
}) => {
    useEffect(() => {
        if (_.isEmpty(employerLoginData)) {
            try {
                AsyncStorage.getItem("employerData").then((user) => {
                    employerLoginData = JSON.parse(user);
                });
            } catch (error) {
                // Error retrieving data
                console.log(error.message);
            }
        }
    }, []);

    return (
        <View style={styles.bottomNavContainer}>
            <AntDesign
                name="home"
                style={styles.bottomNavIcons}
                onPress={() => switchToScreen("EmployerScreen", employerLoginData)}
            />
            <AntDesign
                name="user"
                style={styles.bottomNavIcons}
                onPress={() => switchToScreen("EmployerProfile1", employerLoginData)}
            />
            <AntDesign
                name="pluscircleo"
                style={styles.bottomNavIcons}
                onPress={() => alert('check for paid membership should be applied.')}
            />
            <AntDesign
                name="staro"
                style={styles.bottomNavIcons}
                onPress={() => switchToScreen("StarredCandidate", employerLoginData)}
            />
            <Ionicons
                name="md-more"
                style={styles.bottomNavIcons}
                onPress={() => setSettingModalVisible(true)}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    bottomNavContainer: {
        flexDirection: "row",
        height: "100%",
        textAlign: "center",
    },
    bottomNavIcons: {
        flex: 1,
        fontSize: 30,
        textAlign: "center",
        alignSelf: "center",
        color: "#9E9E9E",
    },
});

export default EmployerBottomNavigator;
