import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ScrollView
  //Image,
} from 'react-native';
import CheckBox from "@react-native-community/checkbox";

import { baseUrl } from '../utils/constants';

export default class AdditionalInfo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      city: null,
      services: [],
      serviceList: [],
      isLoading: false,
      error: '',
      success: true,
      checkedItems: new Map()
    }
  }

  componentWillMount() {
    this.setState({ isLoading: true })
    fetch(baseUrl + 'api/service/list')
      .then(response => response.json())
      .then((response) => {
        this.setState({ isLoading: false })
        if (!response.success)
          throw new Error(response)
        else {
          response.result.map((key, index) => {
            this.setState({
              checkedItems: this.state.checkedItems.set(key.id, false)
            })
          })
          this.setState({ serviceList: response.result, success: true })
        }
      })
      .catch((err) => {
        this.setState({
          isLoading: false,
          success: true,
          error: err
        })
      })
  }
  handleCity = (city) => {
    this.setState({ city })
  }
  handleSubmit = () => {
    // this.props.navigation.navigate('UploadPic')
    this.setState({ isLoading: true })
    fetch(baseUrl + 'api/employer/add-ctiy-services', {
      method: 'POST',
      mode: 'cors', // no-cors, *cors, same-origin
      cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
      credentials: 'same-origin', // include, *same-origin, omit
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      redirect: 'follow', // manual, *follow, error
      referrerPolicy: 'no-referrer',
      body: JSON.stringify({ services: this.state.services, city: this.state.city, user_id: this.props.navigation.getParam('user_id') })
    })
      .then(response => response.json())
      .then((response) => {
        console.log(response)
        if (!response.success)
          throw new Error(response)
        else {
          console.log("here")
          this.props.navigation.navigate('UploadPic', { service_list: this.state.serviceList, user_id: this.props.navigation.getParam('user_id') })
        }
      })
      .catch((err) => {
        this.setState({
          success: false, err: err, isLoading: false
        })
      })
  }
  handlePress = (val) => {
    const arr = this.state.services
    const isChecked = this.state.checkedItems.get(val.id)
    console.log(isChecked)
    !isChecked ? arr.push(val.id) : arr.pop(val.id)
    this.setState({
      services: arr, checkedItems: this.state.checkedItems.set(val.id, !isChecked)
    }, () => {
      console.log(this.state.services)
    })
  }
  render() {
    return (

      <ScrollView>
        <View style={styles.labelHeading}>
          <Text style={styles.loginText}>Additional Information</Text>
        </View>
        <View style={styles.label}>
          <Text style={styles.loginText}>Select Services</Text>
        </View>
        <View style={styles.labelHeading}>
          {
            this.state.serviceList.length > 0 ?
              this.state.serviceList.map((val, index) => {
                return (
                  <View style={styles.checkboxContainer} key={index}>
                    <TouchableOpacity key={val.id} onPress={() => { this.handlePress(val) }}>
                      <CheckBox
                        value={this.state.checkedItems.get(val.id)}
                        name={val.id}
                        onChange={() => { this.handlePress(val) }}
                        style={styles.checkbox}
                      />
                      <Text style={styles.label}>{val.service_name}</Text>
                    </TouchableOpacity>
                  </View>
                )
              }) : null
          }
        </View>
        <View>
          <Text style={styles.loginText}>City</Text>
          <View style={styles.firstnameText}>
            <TextInput
              style={styles.inputText}
              onChangeText={this.handleCity}
            />
          </View>
        </View>
        <View style={styles.loginBtn1}>
          <TouchableOpacity onPress={this.handleSubmit}>
            <Text style={styles.nextBtnText}>Submit</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>

    );
  }
}
const styles = StyleSheet.create({
  nextBtnText: {
    color: 'black',
    fontSize: 20
  },
  container: {
    flex: 1,
    alignItems: "flex-start",
    justifyContent: "center",
  },
  label: {
    margin: 8,
  },
  labelHeading: {
    marginTop: 25
  },
  checkboxContainer: {
    flexDirection: "row",
    marginBottom: 15,
    alignContent: "center",
    justifyContent: 'center'
  },
  inputText: {
    height: 30,
    color: 'black',
    marginTop: -17,
    marginLeft: -50,

  },
  checkbox: {
    alignSelf: "center",
  },
  loginText: {
    color: 'black',
    fontSize: 30,
    marginTop: 20,
    marginLeft: 50,
  },
  firstnameText: {
    flexDirection: 'row',
    width: '45%',
    backgroundColor: '#64358D',
    borderRadius: 25,
    height: 30,
    marginTop: -40,
    marginLeft: 160,
    marginBottom: 10,

    justifyContent: 'center',
    padding: 20,
  },

  loginBtn: {
    width: '20%',
    backgroundColor: '#fb5b5a',
    borderRadius: 25,
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 150,
    marginLeft: 285,
  },
  loginBtn1: {
    width: '20%',
    backgroundColor: '#fb5b5a',
    borderRadius: 25,
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 150,
    //marginBottom: 100,
    marginLeft: 85,
  },
});
