import React from "react";
import {
    StyleSheet,
    Text,
    View,
    TextInput,
    TouchableOpacity,
    ScrollView,
} from "react-native";

import { baseUrl } from "../../utils/constants";
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#ffffff",
        alignItems: "center",
        justifyContent: "center",
    },
    error: {
        color: "red",
        fontSize: 12,
        marginTop: 20,
        marginLeft: 35,
        justifyContent: "center",
    },
    nextBtnText: {
        color: "black",
        fontSize: 20,
    },
    inputText: {
        height: 30,
        color: "black",
        marginTop: -17,
        marginLeft: -50,
    },
    inputText1: {
        height: 30,
        color: "black",
        marginTop: 50,
        marginLeft: 40,
    },
    loginText: {
        color: "black",
        fontSize: 30,
        marginTop: 20,
        //marginBottom: 100,
        marginLeft: 65,
    },
    inputView2: {
        marginBottom: -35,
    },
    firstnameText: {
        flexDirection: "row",
        width: "45%",
        backgroundColor: "#64358D",
        borderRadius: 25,
        height: 30,
        marginTop: -40,
        marginLeft: 160,
        marginBottom: 10,

        justifyContent: "center",
        padding: 20,
    },
    loginBtn: {
        width: "20%",
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: -50,
        //marginBottom: 100,
        marginLeft: 285,
    },
    loginBtn1: {
        width: "20%",
        backgroundColor: "#fb5b5a",
        borderRadius: 25,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 150,
        //marginBottom: 100,
        marginLeft: 85,
    },
});

// make the GET request to fetch data from the URL then using promise function to handle response.
class JobBasicInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            first_name: "",
            last_name: "",
            email: "",
            password: "",
            company_name: "",
            confirm_email: "",
            phone_number: "",
            title: "",
            success: true,
        };
    }
    handleTitleChange = (event) => {
        this.setState({
            title: event,
        });
    };
    handleFirstNameChange = (event) => {
        this.setState({
            first_name: event,
        });
    };
    handleLastNameChange = (event) => {
        this.setState({
            last_name: event,
        });
    };
    handleEmailChange = (event) => {
        this.setState({
            email: event,
        });
    };
    handlePhoneNumberChange = (event) => {
        this.setState({
            phone_number: event,
        });
    };
    handleConfirmEmailChange = (event) => {
        this.setState({
            confirm_email: event,
        });
    };
    handleCompanyChange = (event) => {
        this.setState({
            company_name: event,
        });
    };

    handleSubmit = () => {
        // this.props.navigation.navigate('AddInfo')
        const that = this;
        // that.props.navigation.navigate('AddInfo')
        fetch(baseUrl + "api/job-seeker/registration", {
            method: "POST",
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json",
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer",
            body: JSON.stringify(this.state),
        })
            .then((response) => response.json())
            .then((response) => {
                if (!response.success) throw new Error(response);
                else {
                    that.props.navigation.navigate("JobAdditionalInfo", {
                        user_id: response.result.id,
                    });
                }
            })
            .catch((err) => {
                this.setState({
                    success: false,
                    err: "Registration Failed. Please try again later",
                });
            });
    };

    render() {
        return (
            <ScrollView>
                <View>
                    <Text style={styles.loginText}>Basic Information</Text>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Title/Prefix</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder="Mr/Mrs/Ms "
                            placeholderTextColor="white"
                            onChangeText={this.handleTitleChange}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>First Name</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder="John"
                            placeholderTextColor="white"
                            onChangeText={this.handleFirstNameChange}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Last Name</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder="Doe"
                            placeholderTextColor="white"
                            onChangeText={this.handleLastNameChange}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Company Name</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder="Company Name Ltd"
                            placeholderTextColor="white"
                            onChangeText={this.handleCompanyChange}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Email</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder="john@example.com"
                            placeholderTextColor="white"
                            onChangeText={this.handleEmailChange}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Confirm Email</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder=" "
                            placeholderTextColor="white"
                            onChangeText={this.handleConfirmEmailChange}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Password</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            name="password"
                            type="text"
                            secureTextEntry
                            style={styles.inputText}
                            placeholder="***********"
                            placeholderTextColor="white"
                            onChangeText={(text) => {
                                this.setState({ password: text });
                            }}
                        />
                    </View>
                </View>
                <View style={styles.inputView2}>
                    <Text style={styles.inputText1}>Phone Number</Text>
                    <View style={styles.firstnameText}>
                        <TextInput
                            style={styles.inputText}
                            placeholder=" "
                            placeholderTextColor="white"
                            onChangeText={this.handlePhoneNumberChange}
                        />
                    </View>
                    {!this.state.success && (
                        <View>
                            <Text style={styles.error}>{this.state.err}</Text>
                        </View>
                    )}
                </View>

                <View style={styles.loginBtn1}>
                    <TouchableOpacity onPress={this.handleSubmit}>
                        <Text style={styles.nextBtnText}>Submit</Text>
                    </TouchableOpacity>
                </View>
                <View style={styles.loginBtn}>
                    <TouchableOpacity onPress={this.handleSubmit}>
                        <Text style={styles.nextBtnText}>Next</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>
        );
    }
}
export default JobBasicInfo;
