import React from "react";
import {
    StyleSheet,
    Text,
    View,
    TextInput,
    TouchableOpacity,
    ScrollView,
    FlatList,
} from "react-native";
import _ from "lodash";

import CheckBox from "@react-native-community/checkbox";
import {LinearGradient} from "expo-linear-gradient";
import {baseUrl} from "../../utils/constants";
import {Picker} from "@react-native-community/picker";
import Toast from "react-native-toast-message";
const world_cities = require("../../../assets/jsonContent/world_cities.json");

export default class JobAdditionalInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            city: "",
            city_error: "",
            services: [],
            serviceList: [],
            isLoading: false,
            error: "",
            success: true,
            checkedItems: new Map(),
        };
    }

    componentWillMount() {
        console.log(
            "in job seeker additional info ==========> ",
            this.props.navigation.state.params
        );
        this.setState({
            cities: world_cities
                .filter((city) => city.country === "Canada")
                .sort((a, b) => (a.city > b.city ? 1 : -1)),
            registeredUser: this.props.navigation.state.params,
        });

        fetch(baseUrl + "api/service/list")
            .then((response) => response.json())
            .then((response) => {
                this.setState({isLoading: false});
                if (!response.success) throw new Error(response);
                else {
                    response.result.map((key, index) => {
                        this.setState({
                            checkedItems: this.state.checkedItems.set(
                                key.id,
                                false
                            ),
                        });
                    });
                    this.setState({
                        serviceList: response.result,
                        success: true,
                    });
                }
            })
            .catch((err) => {
                this.setState({
                    isLoading: false,
                    success: true,
                    error: err,
                });
            });
    }
    handleCity = (event) => {
        let regex_name = /^[a-zA-Z ]*$/;
        if (!_.isEmpty(event)) {
            if (!regex_name.test(event)) {
                this.setState({city_error: "only Allow only character"});
            } else {
                this.setState({city: event});
                this.setState({city_error: null});
            }
        } else {
            this.setState({city_error: "City is required"});
        }
    };
    handleSubmit = () => {
        if (this.state.city_error !== null) {
            Toast.show({
                type: "error",
                position: "top",
                text1: "Warning",
                text2: `Fill required fields.`,
            });
        } else {
            // this.props.navigation.navigate('UploadPic')
            this.setState({isLoading: true});
            fetch(baseUrl + "api/job-seeker/add-ctiy-services", {
                method: "POST",
                mode: "cors", // no-cors, *cors, same-origin
                cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
                credentials: "same-origin", // include, *same-origin, omit
                headers: {
                    "Content-Type": "application/json",
                    // 'Content-Type': 'application/x-www-form-urlencoded',
                },
                redirect: "follow", // manual, *follow, error
                referrerPolicy: "no-referrer",
                body: JSON.stringify({
                    services: this.state.services,
                    city: this.state.city,
                    id: this.props.navigation.getParam("id"),
                }),
            })
                .then((response) => response.json())
                .then((response) => {
                    console.log(response);
                    if (!response.success) throw new Error(response);
                    else {
                        this.props.navigation.navigate("JobUploadImage", {
                            user_id: this.props.navigation.getParam("user_id"),
                            registeredUser: this.state.registeredUser,
                        });
                    }
                })
                .catch((err) => {
                    this.setState({success: false, err: err, isLoading: false});
                });
        }
    };
    handlePress = (val) => {
        const arr = this.state.services;
        const isChecked = this.state.checkedItems.get(val.id);
        console.log(isChecked);
        !isChecked ? arr.push(val.id) : arr.pop(val.id);
        this.setState(
            {
                services: arr,
                checkedItems: this.state.checkedItems.set(val.id, !isChecked),
            },
            () => {
                console.log(this.state.services);
            }
        );
    };
    render() {
        return (
            <LinearGradient
                colors={["#BA9ED1", "#765A8D"]}
                style={styles.container}
                //  Linear Gradient
                start={{x: 0, y: 0}}
                end={{x: 0, y: 1}}
            >
                <ScrollView>
                    <View style={styles.labelHeading}>
                        <Text style={styles.headingText}>
                            Additional Information
                        </Text>
                    </View>
                    <View style={styles.label}>
                        <Text style={styles.labelText}>Select Services</Text>
                    </View>
                    <View style={styles.labelHeading}>
                        <View style={styles.MainContainer}>
                            <FlatList
                                data={this.state.serviceList}
                                showsVerticalScrollIndicator={false}
                                renderItem={({item}) => (
                                    <View
                                        style={{
                                            flex: 1,
                                            flexDirection: "column",
                                            margin: 1,
                                        }}
                                    >
                                        <View style={styles.imageThumbnail}>
                                            <CheckBox
                                                value={this.state.checkedItems.get(
                                                    item.id
                                                )}
                                                onValueChange={() =>
                                                    this.handlePress(item)
                                                }
                                            />
                                            <Text>{item.service_name}</Text>
                                        </View>
                                    </View>
                                )}
                                //Setting the number of column
                                numColumns={2}
                                keyExtractor={(item, index) => index}
                            />
                        </View>
                    </View>
                    <View>
                        <View style={styles.rowContainer}>
                            <Text style={styles.labelText}>City</Text>
                            <View style={styles.inputContainer}>
                                <View style={[styles.pickerStyle]}>
                                    <Picker
                                        selectedValue={this.state.city}
                                        onValueChange={(itemValue, itemIndex) =>
                                            this.handleCity(itemValue, "city")
                                        }
                                    >
                                        {this.state.cities.map((city) => {
                                            return (
                                                <Picker.Item
                                                    key={city.id}
                                                    label={city.city}
                                                    value={city.city}
                                                />
                                            );
                                        })}
                                    </Picker>
                                </View>
                                {this.state.city_error ? (
                                    <Text style={styles.errorText}>
                                        {this.state.city_error}
                                    </Text>
                                ) : null}
                            </View>
                        </View>
                    </View>
                    <View style={styles.rowContainer}>
                        <TouchableOpacity
                            style={styles.updateBtn}
                            onPress={this.handleSubmit}
                        >
                            <Text>Submit</Text>
                        </TouchableOpacity>
                    </View>
                </ScrollView>
                <Toast ref={(ref) => Toast.setRef(ref)} />
            </LinearGradient>
        );
    }
}
const styles = StyleSheet.create({
    nextBtnText: {
        color: "black",
        fontSize: 20,
    },
    inputContainer: {
        flex: 2,
    },
    headingText: {
        fontWeight: "800",
        fontSize: 30,
        alignSelf: "center",
        marginTop: 30,
        marginBottom: 20,
    },
    rowContainer: {
        flexDirection: "row",
