        marginHorizontal: 20,
        marginVertical: 10,
    },
    pickerStyle: {
        flex: 2,
        height: 50,
        borderRadius: 5,
        backgroundColor: "#64358D",
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 4,
    },
    inputStyle: {
        flex: 2,
        height: 50,
        overflow: "hidden",
        borderRadius: 5,
        backgroundColor: "#fff",
        paddingLeft: 10,
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 4,
    },
    updateBtn: {
        height: 50,
        width: 180,
        borderRadius: 25,
        backgroundColor: "#fb5b5a",
        shadowColor: "#dddddd",
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.3,
        shadowRadius: 4.65,
        elevation: 8,
        alignItems: "center",
        justifyContent: "center",
    },
    errorText: {
        textTransform: "capitalize",
        color: "red",
        marginLeft: 5,
    },
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
    },
    label: {
        margin: 8,
    },
    labelHeading: {
        marginTop: 25,
    },
    checkboxContainer: {
        flexDirection: "row",
        marginBottom: 15,
        alignContent: "center",
        justifyContent: "center",
    },
    inputText: {
        height: 30,
        color: "black",
        marginTop: -17,
        marginLeft: -50,
    },
    checkbox: {
        alignSelf: "center",
    },
    loginText: {
        color: "black",
        fontSize: 30,
        marginTop: 20,
        alignContent: "center",
        justifyContent: "center",
        marginHorizontal: 30,
        // marginLeft: 50,
    },
    firstnameText: {
        flexDirection: "row",
        width: "45%",
        backgroundColor: "#64358D",
        borderRadius: 25,
        height: 30,
        marginTop: -40,
        marginLeft: 160,
        marginBottom: 10,

        justifyContent: "center",
        padding: 20,
    },

